mapper.select:
SELECT ptr.id, psp.id position, pt.team_id teamId, ptr.maxPlayers, ptr.explainInfo, psp.text, pt.t_name name, pt.t_players_total playersTotal, pt.t_image image, pt.t_province province, pt.t_city city, pt.t_district district, pst.sports_name sportsName FROM playball_team_recruits ptr LEFT JOIN playball_sports_position psp ON psp.id=ptr.position LEFT JOIN playball_team pt ON pt.team_id=ptr.team_id LEFT JOIN playball_sports_type pst ON pst.id=pt.st_id where 1=1
and pt.t_district=#{params.district}
and pt.st_id=#{params.stId}
and ( pt.t_name=LIKE concat('%',#{params.searchValue},'%') or psp.text=LIKE concat('%',#{params.searchValue},'%') )
LIMIT #{params.PageNum},#{params.PageSize}

SELECT COUNT(*) FROM playball_team_recruits ptr LEFT JOIN playball_sports_position psp ON psp.id=ptr.position LEFT JOIN playball_team pt ON pt.team_id=ptr.team_id LEFT JOIN playball_sports_type pst ON pst.id=pt.st_id where 1=1
and pt.t_district=#{params.district}
and pt.st_id=#{params.stId}
and ( pt.t_name=LIKE concat('%',#{params.searchValue},'%') or psp.text=LIKE concat('%',#{params.searchValue},'%') )
