mapper.sql:
from playball.playball_games pg left join playball.playball_sports_type pst on pg.sports_id = pst.id left join playball.playball_field_info pfi on pg.field_id = pfi.id left join playball.sys_depart sd on pfi.business_id = sd.id WHERE pg.is_delete = 0
and pg.games_name LIKE concat(concat('%',#{game.gamesName}),'%')
and pg.sports_id = #{game.sportsId}
