mapper.sql:
FROM playball_game_schedule pgs LEFT JOIN playball_games pg ON pg.id = pgs.games_id LEFT JOIN playball_team ptt ON ptt.team_id = pgs.team_id LEFT JOIN playball_team pto ON pto.team_id = pgs.opponent_id WHERE pg.is_delete = 0
and pg.games_name LIKE concat(concat('%',#{scheduleInfo.gamesName}),'%')
and ptt.t_name = #{scheduleInfo.teamName}
